import pytest
from zipy.web3 import Account, Chain, Contract, CHAIN_GOERLI, to_wei


CHAIN_LOCAL = Chain(
    rpc="http://localhost:7545",
    chain_id=1337,
)


class TestAccount:
    def test_create(self):
        account = Account.create()
        assert account.address
        assert account.private_key

    def test_init(self):
        account = Account.create()
        account2 = Account(account.private_key)
        assert account.private_key == account2.private_key
        assert account.address == account.address

    def test_set_chain(self):
        account = Account.create()
        account2 = Account(account.private_key)

        with pytest.raises(ValueError):
            account2.transaction_count

        account2.set_chain(CHAIN_GOERLI)
        assert account2.transaction_count == 0

        account3 = Account(account.private_key, chain=CHAIN_GOERLI)
        assert account3.transaction_count == 0

    def test_balance_of_eth(self):
        account = Account(
            "0xbabf3911959cd4c053aba0867f01a3b6054a94c185584e8a4fdd74a927d214cb",
            chain=CHAIN_GOERLI,
        )
        assert account.balance_of_eth() == 0.01 * (10**18)

