    def test_transaction_count(self):
        account = Account(
            "0x0a4049fda4717b96118e3326817c1a5214eb3a1a06088f389da33c699196329c",
            chain=CHAIN_GOERLI,
        )
        assert account.transaction_count == 5

        account = Account.create()
        account.set_chain(CHAIN_GOERLI)
        assert account.transaction_count == 0

    def test_transfer_eth(self):
        account = Account(
            "0xb48e38836ad9f26e490c2b18bbfe77c1f638d3bb6dfc7c76a19806028b80f742",
            chain=CHAIN_GOERLI,
        )
        account2 = Account(
            "0xe24fe7983daec6ecbb0bda3e6a7ed9f173b59d07c002f1edce38e57dfbe8ba3d",
            chain=CHAIN_GOERLI,
        )
        account2_balance = account2.balance_of_eth()

        account.transfer_eth(
            to_address=account2.address,
            amount=to_wei(0.1, "gwei"),
        )
        assert account2.balance_of_eth() == account2_balance + to_wei(0.1, "gwei")

